import { combineReducers } from "redux";

const initialDetails = {
    name: "",
    email: "",
    contact: "",
    dob: "",
    condition: "",
    visited: "",
    complete: 0
};

const detailsFormReducer = (state = initialDetails, action) => {
    var formCompleted = 1;
    const formFields = ["name", "email", "contact", "dob", "condition", "visited"];
    switch(action.type) {
        case 'details/name':
            Object.assign(initialDetails, {name : action.payload});
            for (let i = 0; i< formFields.length; i++) {
                if (initialDetails.formFields[i] == "") {
                    formCompleted = 0;
                }
            }
            return Object.assign(initialDetails, {complete : formCompleted});
        case 'details/email':
            Object.assign(initialDetails, {email : action.payload})
            for (let i = 0; i< formFields.length; i++) {
                if (initialDetails.formFields[i] == "") {
                    formCompleted = 0;
                }
            }
            return Object.assign(initialDetails, {complete : formCompleted});
        case 'details/tel':
            Object.assign(initialDetails, {contact : action.payload})
            for (let i = 0; i< formFields.length; i++) {
                if (initialDetails.formFields[i] == "") {
                    formCompleted = 0;
                }
            }
            return Object.assign(initialDetails, {complete : formCompleted});
        case 'details/dob':
            Object.assign(initialDetails, {dob : action.payload})
            for (let i = 0; i< formFields.length; i++) {
                if (initialDetails.formFields[i] == "") {
                    formCompleted = 0;
                }
            }
            return Object.assign(initialDetails, {complete : formCompleted});
        case 'details/condition':
            Object.assign(initialDetails, {condition: action.payload})
            for (let i = 0; i< formFields.length; i++) {
                if (initialDetails.formFields[i] == "") {
                    formCompleted = 0;
                }
            }
            return Object.assign(initialDetails, {complete : formCompleted});
        case 'details/visited':
            Object.assign(initialDetails, {visited : action.payload})
            for (let i = 0; i< formFields.length; i++) {
                if (initialDetails.formFields[i] == "") {
                    formCompleted = 0;
                }
            }
            return Object.assign(initialDetails, {complete : formCompleted});
        default:
            return state
    }
}

export default detailsFormReducer;
