import "../App.css";
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useState } from "react";
import Header from "./Header";

function Registration() {
    const [visited, setVisited] = useState({visited: false});
    function handleClick(event) {
        event.preventDefault();
        console.log(visited);
        if (visited == true) {
            event.target.style.backgroundColor = "#e5e5e5";
            setVisited(false);
        }
        else {
            event.target.style.backgroundColor = "#fca311";
            setVisited(true);
        }
     }

    return <div className="registration">
        <Header />
        <div className="registrationContent">
            <div className="bookingCard">
                <p className="bookingHeading">Patient Details</p>
                <form className="registrationForm">
                    <input type="text" name="name" placeholder="Name" />
                    <input type="text" name="nric" placeholder="NRIC" />
                    <div className="registrationDOB">
                        <span>Date Of Birth:</span>
                        <input type="date" name="dob" />
                    </div>
                    <input type="text" name="contactnumber" placeholder="Contact Number" />
                    <input type="text" name="condition" placeholder="Brief Description of Condition" />
                    <div className="visitedPatient">
                        <p>Have you visited us before?</p>
                        <button className={visited ? "pillButton selected" : "pillButton notSelected"} onClick={() => setVisited(true)}>Yes</button>
                        <button className={visited ? "pillButton notSelected" : "pillButton selected"}  onClick={() => setVisited(false)}>No</button>
                    </div>
                    <div className="bookingNext">
                        <button className="pillButton">Next</button>
                    </div>
                </form>
            </div>
        </div>
    </div>
}

export default Registration;
